const flowData = flow.get("flow");
const timers = flowData.config.timers;

const method = msg?.payload?.method || msg?.method;
const params = msg?.payload?.params || msg?.params || {};
const target = params.target;
const type = params.type;

const out = [[], [], []]; // Port 1,2,3
let runtime = context.get("runtime") || {};

function toMs(val, unit) {
  const map = { s: 1000, min: 60000, h: 3600000, d: 86400000 };
  return val * (map[unit] ?? 1000);
}

function pushMessage(timer, passKey, out, runtime = {}, target = null) {
  const entry = timer.pass?.[passKey];
  if (!entry) return;

  const message = RED.util.cloneMessage(entry);
  const port = (entry.port ?? 0) - 1;
  if (port < 0 || port > 2) return;

  delete message.port;

  if (message.params && target && runtime[target]) {
    for (const [key, val] of Object.entries(message.params)) {
      if (val === null && runtime[target][key] !== undefined) {
        message.params[key] = runtime[target][key];
      } else if (typeof val === "object" && val !== null && "val" in val) {
        message.params[key].val = runtime[target].count;
      }
    }

    if (timer.form === "counter" && passKey === 4 && message.params.val === undefined) {
      message.params.val = runtime[target].count;
    }
  }

  out[port].push({ payload: message });
}

function timerOn(timer, rt, now, out, target, forcedCount = null) {
  rt.state = "on";

  switch (timer.form) {
    case "delay":
      rt.on_time = now;
      rt.done = false;
      rt.phase_until = now + toMs(timer.duration, timer.unit);
      pushMessage(timer, 1, out);
      break;

    case "pwm":
      rt.on_time = now;
      rt.phase = "a";
      rt.phase_until = now + toMs(timer.t_duty, timer.unit);
      pushMessage(timer, 1, out);
      break;

    case "counter":
      rt.on_time = now;
      rt.count = forcedCount !== null ? forcedCount : timer.base;
      rt.next_time = now + toMs(timer.interval, timer.unit);

      const entry4 = timer.pass?.[4];
      if (entry4) {
        entry4.params = entry4.params || {};
        delete entry4.params.val; // runtime[target].count koyabilmesi i√ßin
      }

      pushMessage(timer, 1, out, runtime, target);
      pushMessage(timer, 4, out, runtime, target);
      break;
  }
}

function timerOff(timer, rt, out, target, suppressMessages = false) {
  rt.state = "off";

  switch (timer.form) {
    case "delay":
      rt.done = true;
      if (!suppressMessages) pushMessage(timer, 2, out);
      break;

    case "pwm":
      rt.phase = null;
      if (!suppressMessages) pushMessage(timer, 2, out);
      break;

    case "counter":
      rt.count = 0;
      if (!suppressMessages) {
        pushMessage(timer, 3, out, runtime, target);
        pushMessage(timer, 4, out, runtime, target);
      }

      // day_counter i√ßin evt day 0 mesajƒ±
      if (target === "day_counter") {
        out[2].push({
          payload: {
            method: "evt",
            params: { type: "day", val: 0 }
          }
        });
      }
      break;
  }

  // Genel reset
  rt.on_time = null;
  rt.next_time = null;
  rt.phase_until = null;
  rt.phase = null;
}

function timerTick(timers, runtime, now, out) {
  for (const [key, timer] of Object.entries(timers)) {
    const rt = runtime[key];
    if (!rt || rt.state !== "on") continue;

    switch (timer.form) {
      case "delay":
        if (!rt.done && now >= rt.phase_until) {
          rt.done = true;
          pushMessage(timer, 2, out);
        }
        break;

      case "pwm":
        if (now >= rt.phase_until) {
          if (rt.phase === "a") {
            rt.phase = "b";
            rt.phase_until = now + toMs(timer.t_cycle - timer.t_duty, timer.unit);
            pushMessage(timer, 2, out);
          } else {
            rt.phase = "a";
            rt.phase_until = now + toMs(timer.t_duty, timer.unit);
            pushMessage(timer, 1, out);
          }
        }
        break;

      case "counter":
        if (now >= rt.next_time && rt.count > 0) {
          rt.count -= 1;
          rt.next_time = now + toMs(timer.interval, timer.unit);

          if (timer.signal !== undefined && rt.count === timer.signal) {
            pushMessage(timer, 2, out, runtime, key);
          }

          pushMessage(timer, 4, out, runtime, key);

          if (rt.count === 0) {
            rt.state = "off";
            pushMessage(timer, 3, out, runtime, key);

            if (key === "day_counter") {
              out[2].push({
                payload: {
                  method: "evt",
                  params: { type: "day", val: 0 }
                }
              });
            }

            rt.on_time = null;
            rt.next_time = null;
            rt.phase_until = null;
            rt.phase = null;
          }
        }
        break;
    }
  }
}

// --- CMD handling ---
if (method === "cmd") {
  // --- Special skip for day_counter ---
  if (type === "skip" && target === "day_counter") {
    if (!runtime[target]) runtime[target] = {};
    const rt = runtime[target];

    timerOn(timers[target], rt, Date.now(), out, target, timers[target].signal);

  } else if (timers[target]) {
    if (!runtime[target]) runtime[target] = {};
    if (type === "on") {
      timerOn(timers[target], runtime[target], Date.now(), out, target);
    } else if (type === "off") {
      timerOff(timers[target], runtime[target], out, target);
    }
  } else if (type === "off" && target === "timers") {
    for (const [key, timer] of Object.entries(timers)) {
      if (!runtime[key]) runtime[key] = {};
      timerOff(timer, runtime[key], out, key, true);
    }
  } else {
    out[1].push(msg);
  }

} else if (method === "evt" && type === "timer_tick") {
  timerTick(timers, runtime, Date.now(), out);
} else {
  out[1].push(msg);
}

context.set("runtime", runtime);

return out.some(arr => arr.length) ? out : null;

const flowData = flow.get("flow");
const runtime = flowData.runtime;
let currentState = runtime.fsm.val;

const payload = msg.payload;
if (!payload || typeof payload !== "object") return null;

const method = payload.method;
const params = payload.params || {};
const type = params.type;
const target = params.target;
const val = params.val;

const outputs = [[], []];

function transition(toState) {
  if (toState !== currentState) {
    currentState = toState;
    flowData.runtime.fsm.val = currentState;
    flow.set("flow", flowData);
    sendEvt("fsm", { val: currentState });
  }
}

function sendCmd(type, target, extraParams = {}) {
  const msg = {
    payload: {
      method: "cmd",
      params: { type, target, ...extraParams }
    }
  };
  outputs[0].push(msg);
}

function sendEvt(type, extraParams = {}) {
  const msg = {
    payload: {
      method: "evt",
      params: { type, ...extraParams }
    }
  };
  outputs[1].push(msg);
}

// --- Ana mesaj i≈üleme ---
if (method === "evt") {

  if (type === "oxygen_detector_dig_low") {
    if (val === "off") {
      if (currentState === "start") {
        sendCmd("on", "fan_pwm");
      }      
    }
  } else if (type === "oxygen_detector_dig_high") {
    if (val === "off") {
      if (currentState === "start") {
        sendCmd("off", "fan_pwm");
      }      
    }
  } else if (type === "humidity_detector_ang_low") {
    if (val === "off") {
      if (currentState === "start") {
        sendCmd("on", "water_valve_pwm");
      }      
    }
  } else if (type === "humidity_detector_ang_high") {
    if (val === "off") {
      if (currentState === "start") {
        sendCmd("off", "water_valve_pwm");
      }      
    }
  }

} else if (method === "cmd") {

  if (target === "fsm") {

    if (type === "start") {
      if (runtime.oxygen_detector_dig_high === "on") {
        sendCmd("on", "fan_pwm");
      } else {
        sendCmd("off", "fan_pwm");
      }
      if (runtime.humidity_detector_ang_high === "on") {
        sendCmd("on", "water_valve_pwm");
      } else {
        sendCmd("off", "water_valve_pwm");
      }    
      sendCmd("on", "day_counter");
      transition("start");

    } else if (type === "dry") {      
      sendCmd("on", "fan_pwm");
      sendCmd("off", "water_valve_pwm");
      sendCmd("skip", "day_counter");
      transition("dry");

    } else if (type === "end") {
      sendCmd("off", "timers");
      sendCmd("off", "actuators");
      transition("end");
    }

  } else {
    outputs[0].push(msg);
  }
}

return outputs;

// Sabitler
const TELEMETRY_TOPIC = "v1/devices/me/telemetry";
const RPC_RESPONSE_PREFIX = "v1/devices/me/rpc/response/";

const topic = msg.topic || "";
const payload = msg.payload || {};
const method = payload?.method || "";
const params = payload?.params || {};

const flowData = flow.get("flow") || {};

// G√ºvenli msgId
const msgId = (topic.split("/").pop()) || "unknown";

function getValueAtPath(obj, path) {
    if (!Array.isArray(path) || path.length === 0) return obj;
    return path.reduce((acc, key) => (acc && acc[key] !== undefined ? acc[key] : null), obj);
}

function setValueAtPath(obj, path, value) {
    if (!Array.isArray(path) || path.length === 0) {
        throw new Error("Invalid path for set");
    }
    let current = obj;
    for (let i = 0; i < path.length - 1; i++) {
        const key = path[i];
        if (!(key in current) || typeof current[key] !== 'object' || current[key] === null) {
            current[key] = {};
        }
        current = current[key];
    }
    current[path[path.length - 1]] = value;
}

function buildRpcResponse(id, content) {
    return {
        topic: RPC_RESPONSE_PREFIX + id,
        payload: content
    };
}

function buildTelemetryMsg(content) {
    return {
        topic: TELEMETRY_TOPIC,
        payload: content
    };
}

// 1) EVT ‚Äî esnek versiyon
if (method === "evt") {
    const subType = params?.type;

    // Geri kalan t√ºm evt‚Äôler i√ßin esnek format:
    // params.type dƒ±≈üƒ±ndaki her ≈üeyi otomatik kapsar
    if (subType) {
        // type dƒ±≈üƒ±ndaki t√ºm alanlarƒ± bir objeye kopyala
        const { type, ...rest } = params;
        // √∂rn. { motor1: {x:10,y:20}} gibi
        const formatted = { [subType]: rest };
        return [buildTelemetryMsg(formatted), null, null];
    }

    // params.type yoksa: params doƒürudan telemetry payload olarak g√∂nderilir
    return [buildTelemetryMsg(params), null, null];
}

// 2) CMD
if (method === "cmd") {
    // √ñzel "inject ‚Üí flow_context" komutu kontrol√º
    if (params?.type === "inject" && params?.target === "flow_context") {
        return [buildRpcResponse(msgId, { response: true }), null, { payload }];
    }
    // Normal cmd
    return [buildRpcResponse(msgId, { response: true }), { payload }, null];
}

// 3) set/get tipi dot-path'li method'lar
const [action, ...pathParts] = method.split(".");

if (action === "get") {
    try {
        const value = getValueAtPath(flowData, pathParts);
        return [buildRpcResponse(msgId, { value: value === undefined ? null : value }), null, null];
    } catch (err) {
        return [buildRpcResponse(msgId, { error: err.message }), null, null];
    }
}

if (action === "set") {
    try {
        if (!pathParts || pathParts.length === 0) {
            return [buildRpcResponse(msgId, { error: "Invalid set path" }), null, null];
        }
        const newValue = params?.value;
        setValueAtPath(flowData, pathParts, newValue);
        flow.set("flow", flowData);
        return [buildRpcResponse(msgId, { response: true }), null, null];
    } catch (err) {
        return [buildRpcResponse(msgId, { error: err.message }), null, null];
    }
}

// Bilinmeyen method'lar
return [null, null, null];

const flowData = flow.get("flow");
const analogChannels = flowData.config.io.analog_inputs.channels;
const runtime = flowData.runtime;

const m = msg.payload;
if (typeof m !== "object") return null;

const key = m.params?.type;
const val = m.params?.val;
if (typeof key !== "string" || typeof val !== "number") return null;

const channelDef = analogChannels[key];
if (!channelDef || !channelDef.pro) return null;

const changed = [];

// √∂rn. humidity.pro = { humidity_detector_ang: { low: 30, high: 60 } }
for (const [detectorKey, thresholds] of Object.entries(channelDef.pro)) {
    if (!thresholds || typeof thresholds.low !== "number" || typeof thresholds.high !== "number") continue;

    // 2 key olu≈ütur: ..._ang_low ve ..._ang_high
    const lowKey = `${detectorKey}_low`;
    const highKey = `${detectorKey}_high`;

    // √∂nce mevcut durumlarƒ± oku
    const prevLow = runtime[lowKey]?.val ?? "on";
    const prevHigh = runtime[highKey]?.val ?? "on";

    let newLow = prevLow;
    let newHigh = prevHigh;

    // low alanƒ± i√ßin kural: val < low ‚Üí off, aksi ‚Üí on
    if (val < thresholds.low) newLow = "off";
    else newLow = "on";

    // high alanƒ± i√ßin kural: val > high ‚Üí off, aksi ‚Üí on
    if (val > thresholds.high) newHigh = "off";
    else newHigh = "on";

    // deƒüi≈üim varsa runtime'ƒ± g√ºncelle ve mesaj olu≈ütur
    if (newLow !== prevLow) {
        runtime[lowKey] = { val: newLow };
        changed.push({
            payload: {
                method: "evt",
                params: { type: lowKey, val: newLow }
            }
        });
    }

    if (newHigh !== prevHigh) {
        runtime[highKey] = { val: newHigh };
        changed.push({
            payload: {
                method: "evt",
                params: { type: highKey, val: newHigh }
            }
        });
    }
}

// yalnƒ±zca deƒüi≈üiklik varsa flow context'i g√ºncelle
if (changed.length) {
    flow.set("flow", flowData);
    return [changed];
}

return null;

// === SIN_READ..JS ===
// Bu fonksiyon nodu, her tetiklendiƒüinde t√ºm sinamics s√ºr√ºc√ºlerinin
// ZSW Status Word registerlerini okumak i√ßin mesaj √ºretir.
// √áƒ±kƒ±≈üƒ± sin_queue'ya baƒülƒ±dƒ±r.

const flowData = flow.get("flow");
if (!flowData?.config?.sinamics) return null;

// S√ºr√ºc√º listesi flow context'ten alƒ±nƒ±yor
const sinamicsChannels = flowData.config.sinamics.channels;
const address = 109;   // ZSW Status Word register adresi
const quantity = 1;    // Okunacak register sayƒ±sƒ±

// Mesajlarƒ± olu≈ütur
const msgs = Object.entries(sinamicsChannels).map(([name, cfg]) => {
    const unitid = cfg.unitid;

    return {
        unitid: unitid, // ‚úÖ √ºst property olarak
        payload: {
            fc: 3,
            unitid: unitid,  // yine payload i√ßinde de bƒ±rakƒ±yoruz (Modbus i√ßin)
            address: address,
            quantity: quantity
        }
    };
});

// Tek √ßƒ±kƒ±≈ütan d√∂rt mesaj olarak g√∂ndermek i√ßin:
return [msgs];

// === SIN_WRITE.JS ===
// Bu node gelen "cmd" mesajlarƒ±na g√∂re sinamics s√ºr√ºc√ºlerine modbus write mesajlarƒ± olu≈üturur
// √áƒ±kƒ±≈üƒ± sin_queue'ya baƒülƒ± olacak, evt mesajlarƒ± √ºretmeyecek

const flowData = flow.get("flow");
if (!flowData || !flowData.config || !flowData.config.sinamics) return null;

const config = flowData.config;
const runtime = flowData.runtime;
const payload = msg.payload;

if (!payload || payload.method !== "cmd" || typeof payload.params !== "object") return null;

const { type, target, set_point } = payload.params;
if (!type || !target) return null;

const sinamicsChannels = config.sinamics.channels;
const commandWords = config.sinamics.command_words;
const speedMax = config.sinamics.speed_max || 16384;
const powerUpTime = config.sinamics.power_up_time || 15000;

// Speed helper (default=100)
function getSpeedValue(sp) {
    const spVal = (typeof sp === "number") ? sp : 100; // default 100
    return Math.round(spVal / 100 * speedMax);
}

// Modbus write mesajƒ± olu≈ütur
function createModbusMsg(value, unitid, address, quantity = 1) {
    return { payload: { value, fc: 6, unitid, address, quantity } };
}

// üîπ TOPLU OFF ‚Äì sadece off komut word g√∂nderir ve runtime g√ºnceller
function setAllOff(runtimeObj) {
    const msgs = [];
    for (const [name, sin] of Object.entries(sinamicsChannels)) {
        const unitid = sin.unitid;

        const offVal = commandWords.off;
        if (offVal != null) {
            msgs.push(createModbusMsg(offVal, unitid, 99, 1));
        }

        // Runtime g√ºncelle
        if (!runtimeObj[name]) runtimeObj[name] = {};
        runtimeObj[name].val = "off";
    }
    return msgs;
}

// üîπ TOPLU SPEED ‚Äì her cihaz i√ßin speed_set_point g√∂nderir
function setAllSpeed(runtimeObj) {
    const msgs = [];
    for (const [name, sin] of Object.entries(sinamicsChannels)) {
        const unitid = sin.unitid;

        const speedValue = getSpeedValue(runtimeObj[name]?.speed_set_point);
        msgs.push(createModbusMsg(speedValue, unitid, 100, 1));
    }
    return msgs;
}

// üîπ TOPLU FAULT ACK
function ackAllFault() {
    const msgs = [];
    const faultAckSet = commandWords.fault_ack_set;
    const faultAckRes = commandWords.fault_ack_res;

    if (faultAckSet == null || faultAckRes == null) {
        node.warn("fault_ack_set veya fault_ack_res tanƒ±mlƒ± deƒüil.");
        return msgs;
    }

    for (const [name, sin] of Object.entries(sinamicsChannels)) {
        msgs.push(createModbusMsg(faultAckSet, sin.unitid, 99, 1));
    }
    for (const [name, sin] of Object.entries(sinamicsChannels)) {
        msgs.push(createModbusMsg(faultAckRes, sin.unitid, 99, 1));
    }
    return msgs;
}

// Mesajlarƒ± tek √ßƒ±kƒ±≈üa g√∂nder
function sendMsgs(msgs) {
    for (const m of msgs) node.send(m);
}

let modbusMsgs = [];

/* üîπ Fault Acknowledgement t√ºm sinamics i√ßin */
if (type === "fault_ack" && target === "sinamics") {
    modbusMsgs = ackAllFault();
    sendMsgs(modbusMsgs);

/* üîπ Actuators OFF ‚Äì eski davranƒ±≈ü korunuyor */
} else if (type === "off" && target === "actuators") {
    modbusMsgs = [
        ...setAllOff(runtime),
        ...setAllSpeed(runtime)
    ];
    sendMsgs(modbusMsgs);

/* üîπ Power OFF ‚Äì hi√ßbir i≈ülem yapmadan √ßƒ±k, ama if dursun */
} else if (type === "off" && target === "power") {
    // ƒ∞stenilen ≈üekilde hi√ßbir ≈üey yapƒ±lmƒ±yor
    return null;

/* üîπ Power ON ‚Äì √∂nce bekle, sonra off + speed g√∂nder (runtime‚Äôƒ± g√ºncel √ßekerek) */
} else if (type === "on" && target === "power") {
    setTimeout(() => {
        // 10 sn sonra g√ºncel flowData √ßek
        const currentFlowData = flow.get("flow") || {};
        if (!currentFlowData.runtime) currentFlowData.runtime = {};

        const msgs = [
            ...setAllOff(currentFlowData.runtime),
            ...setAllSpeed(currentFlowData.runtime)
        ];
        sendMsgs(msgs);

        // g√ºncel runtime‚Äôƒ± tekrar kaydet
        flow.set("flow", currentFlowData);
    }, powerUpTime);

/* üîπ Bireysel sinamics hedefleri (forward/reverse/off/speed) */
} else if (sinamicsChannels[target]) {
    const unitid = sinamicsChannels[target].unitid;

    if (["forward", "reverse", "off"].includes(type)) {
        // 1Ô∏è‚É£ Speed register (speed_set_point, default 100) ‚Äî √∂nce g√∂nder
        const speedValue = getSpeedValue(runtime[target]?.speed_set_point);
        modbusMsgs.push(createModbusMsg(speedValue, unitid, 100, 1));

        // 2Ô∏è‚É£ Komut word (forward/reverse/off) ‚Äî sonra g√∂nder
        const value = commandWords[type];
        if (value != null) {
            modbusMsgs.push(createModbusMsg(value, unitid, 99, 1));
            if (!runtime[target]) runtime[target] = {};
            runtime[target].val = type;
        }
    }

    if (type === "speed" && typeof set_point === "number") {
        const speedValue = getSpeedValue(set_point);
        modbusMsgs.push(createModbusMsg(speedValue, unitid, 100, 1));
        if (!runtime[target]) runtime[target] = {};
        runtime[target].speed_set_point = set_point;   // artƒ±k speed_set_point
    }

    sendMsgs(modbusMsgs);
} else {
    return null; // Sadece sinamics target'larƒ±nƒ± i≈üler
}

// g√ºncel flowData‚Äôyƒ± kaydet
flow.set("flow", flowData);
return null;

/****************************************************
 * sin_queue ‚Äì dispatcher (write √∂ncelikli)
 * Port 1 ‚Üí sin_getter  (fc:3 read)
 * Port 2 ‚Üí sin_writer  (fc:6 write)
 ****************************************************/

// --- Config‚Äôten queue_interval √ßek ---
let flowData = flow.get("flow") || {};
let queueInterval = 100; // default
if (flowData.config?.sinamics?.queue_interval) {
    queueInterval = flowData.config.sinamics.queue_interval;
}

// --- Kuyruklarƒ± node context‚Äôte tut ---
let queueWrite  = context.get("queueWrite")  || [];
let queueRead   = context.get("queueRead")   || [];
let timerActive = context.get("timerActive") || false;

// --- fc alanƒ±nƒ± payload‚Äôdan √ßek ---
const fc = msg?.payload?.fc;

// --- Mesajƒ± ilgili kuyruƒüa at ---
if (fc === 6) {
    // Write mesajƒ±
    queueWrite.push(msg);
} else if (fc === 3) {
    // Read mesajƒ±
    queueRead.push(msg);
} else {
    // fc tanƒ±mlƒ± deƒüilse g√∂rmezden gel
    return null;
}

// --- Kuyruklarƒ± context‚Äôe yaz ---
context.set("queueWrite", queueWrite);
context.set("queueRead", queueRead);

// --- Timer ba≈ülatƒ±lmamƒ±≈üsa ba≈ülat ---
if (!timerActive) {
    context.set("timerActive", true);
    sendNext();
}

return null; // ≈üimdilik mesaj d√∂nd√ºrm√ºyoruz, node.send() kullanƒ±yoruz

/****************************************************
 * Fonksiyonlar
 ****************************************************/
function sendNext() {
    // Kuyruklarƒ± oku
    let qW = context.get("queueWrite") || [];
    let qR = context.get("queueRead")  || [];

    let nextMsg = null;
    let port = null;

    // √ñncelik: √∂nce write kuyruƒüu
    if (qW.length > 0) {
        nextMsg = qW.shift();
        port = 2; // sin_writer √ßƒ±kƒ±≈üƒ±
    } else if (qR.length > 0) {
        nextMsg = qR.shift();
        port = 1; // sin_getter √ßƒ±kƒ±≈üƒ±
    }

    // Kuyruklarƒ± geri kaydet
    context.set("queueWrite", qW);
    context.set("queueRead", qR);

    if (nextMsg) {
        // ƒ∞ki √ßƒ±kƒ±≈ülƒ± fonksiyon nodu: [out1, out2]
        node.send([
            port === 1 ? nextMsg : null,
            port === 2 ? nextMsg : null
        ]);

        // queue_interval sonra tekrar √ßalƒ±≈ütƒ±r
        setTimeout(sendNext, queueInterval);
    } else {
        // Kuyruklar bo≈ü, timer‚Äôƒ± durdur
        context.set("timerActive", false);
    }
}

